import React, { Component } from "react";

import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton} from 'react-mdl';
class Project extends Component {

	constructor(props){
		super(props)
		this.state = { activeTab: 0};
	}

	toggleCategories(){

		if(this.state.activeTab == 0){
		return(
						<Card shadow={5} style={{width: '512px', margin: 'auto'}}>
			    <CardTitle style={{color: '#fff', height: '176px', background: 'url(data:image/png;base64,) center / cover'}}>React Projects</CardTitle>
			    <CardText>
			        My React Apps are fun short projects that showcase the flexibiility of React in web app creation.
			    </CardText>
			    <CardActions border>


			    
			        <Button colored>Explore</Button>



			    </CardActions>
			    <CardMenu style={{color: '#fff'}}>
			        <IconButton name="share" />
			    </CardMenu>
			</Card>
			)
	}
	else if (this.state.activeTab ==1){
		return(
			<Card shadow={5} style={{width: '512px', margin: 'auto'}}>
			    <CardTitle style={{color: '#fff', height: '176px', background: 'url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>Welcome</CardTitle>
			    <CardText>
			        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
			        Mauris sagittis pellentesque lacus eleifend lacinia...
			    </CardText>
			    <CardActions border>
			        <Button colored>Explore</Button>
			    </CardActions>
			    <CardMenu style={{color: '#fff'}}>
			        <IconButton name="share" />
			    </CardMenu>
			</Card>
			)
	}
	else if (this.state.activeTab ==2){
		return(
			<Card shadow={5} style={{width: '512px', margin: 'auto'}}>
			    <CardTitle style={{color: '#fff', height: '176px', background: 'url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>Welcome</CardTitle>
			    <CardText>
			        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
			        Mauris sagittis pellentesque lacus eleifend lacinia...
			    </CardText>
			    <CardActions border>
			        <Button colored>Explore</Button>
			    </CardActions>
			    <CardMenu style={{color: '#fff'}}>
			        <IconButton name="share" />
			    </CardMenu>
			</Card>
			)
	}
	else if (this.state.activeTab ==3){
		return(
			<Card shadow={5} style={{width: '512px', margin: 'auto'}}>
			    <CardTitle style={{color: '#fff', height: '176px', background: 'url(http://www.getmdl.io/assets/demos/welcome_card.jpg) center / cover'}}>Welcome</CardTitle>
			    <CardText>
			        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
			        Mauris sagittis pellentesque lacus eleifend lacinia...
			    </CardText>
			    <CardActions border>
			        <Button colored>Explore</Button>
			    </CardActions>
			    <CardMenu style={{color: '#fff'}}>
			        <IconButton name="share" />
			    </CardMenu>
			</Card>
			)
	}


	}
	
	render(){
		return (

			<div className="category-tabs"> <h1> Projects</h1>

			<Tabs activeTab = {this.state.activeTab} onChange={(tabId) => this.setState({activeTab: tabId})} ripple>
					<Tab>React </Tab>
					
					<Tab>CRUD </Tab>

					<Tab>Embedded Systems </Tab>

					<Tab>Data Analysis </Tab>


				</Tabs>


				<section className = "projects-grid">
					<Grid className = "projects-grid">
						<Cell col={12}>
						<div className="content">{this.toggleCategories()} </div>
						</Cell>

					</Grid>


					
				</section>
			 </div>
				
			)

	};
}

export default Project;
